$(document).ready(function(){$(function(){$("#zond-date").daterangepicker({autoUpdateInput:!1,locale:{cancelLabel:"Clear",format:"DD.MM.YYYY"}}),$("#zond-date").on("apply.daterangepicker",function(a,e){$(this).val(e.startDate.format("DD/MM/YYYY")+" - "+e.endDate.format("DD/MM/YYYY"))}),$("#zond-date").on("cancel.daterangepicker",function(a,e){$(this).val("")})}),$(function(){$("#zond-date-form").daterangepicker({autoUpdateInput:!1,locale:{cancelLabel:"Clear",format:"DD.MM.YYYY"}}),$("#zond-date-form").on("apply.daterangepicker",function(a,e){$(this).val(e.startDate.format("DD/MM/YYYY")+" - "+e.endDate.format("DD/MM/YYYY"))}),$("#zond-date-form").on("cancel.daterangepicker",function(a,e){$(this).val("")})})});